import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

export default function App() {
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);
  const [history, setHistory] = useState([]); // Initialize as empty array
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const analyzeText = async () => {
    try {
      setLoading(true);
      const response = await axios.post('http://localhost:5000/analyze', { text });
      setResult(response.data);
      await fetchHistory();
    } catch (err) {
      setError(err.message);
      console.error("Analysis error:", err);
    } finally {
      setLoading(false);
    }
  };

  const fetchHistory = async () => {
    try {
      const response = await axios.get('http://localhost:5000/history');
      // Ensure we're working with an array
      const historyData = Array.isArray(response.data?.data) ? response.data.data : [];
      setHistory(historyData);
    } catch (err) {
      setError("Failed to load history");
      console.error("History fetch error:", err);
    }
  };

  useEffect(() => { 
    fetchHistory(); 
  }, []);

  // Prepare chart data safely
  const chartData = {
    labels: ['Positive', 'Negative', 'Neutral'],
    datasets: [{
      data: [
        history.filter(item => item?.sentiment === 'positive').length,
        history.filter(item => item?.sentiment === 'negative').length,
        history.filter(item => item?.sentiment === 'neutral').length,
      ],
      backgroundColor: ['#4CAF50', '#F44336', '#9E9E9E'],
    }],
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Real-Time Sentiment Analysis</h1>
      
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {loading && <div>Loading...</div>}

      <textarea 
        value={text} 
        onChange={(e) => setText(e.target.value)} 
        placeholder="Type something..."
        style={{ width: '300px', height: '100px' }}
      />
      <br />
      <button onClick={analyzeText} disabled={loading}>
        {loading ? 'Analyzing...' : 'Analyze'}
      </button>
      
      {result && (
        <div>
          <h3>Result: {result.sentiment} (Score: {result.polarity?.toFixed(2)})</h3>
        </div>
      )}

      <h2>Sentiment Distribution</h2>
      {history.length > 0 ? (
        <Pie data={chartData} />
      ) : (
        <p>No data available for chart</p>
      )}
      
      <h2>Recent Analyses</h2>
      {history.length > 0 ? (
        <ul>
          {history.map((item, index) => (
            <li key={index}>
              "{item.text}" â†’ <strong>{item.sentiment}</strong>
            </li>
          ))}
        </ul>
      ) : (
        <p>No analysis history yet</p>
      )}
    </div>
  );
}
