import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

export default function App() {
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);
  const [history, setHistory] = useState([]);

  const analyzeText = async () => {
    try {
      const response = await axios.post('http://localhost:5000/analyze', { text });
      setResult(response.data);
      fetchHistory();
    } catch (error) {
      console.error("Analysis error:", error);
      alert("Failed to analyze text. Please check backend connection.");
    }
  };
  
  const fetchHistory = async () => {
    try {
      const response = await axios.get('http://localhost:5000/history');
      setHistory(response.data);
    } catch (error) {
      console.error("History fetch error:", error);
    }
  };

  useEffect(() => { fetchHistory(); }, []);

  const chartData = {
    labels: ['Positive', 'Negative', 'Neutral'],
    datasets: [{
      data: [
        history.filter(item => item.sentiment === 'positive').length,
        history.filter(item => item.sentiment === 'negative').length,
        history.filter(item => item.sentiment === 'neutral').length,
      ],
      backgroundColor: ['#4CAF50', '#F44336', '#9E9E9E'],
    }],
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center' }}>Sentiment Analysis</h1>
      
      <div style={{ marginBottom: '2rem' }}>
        <textarea 
          value={text} 
          onChange={(e) => setText(e.target.value)} 
          placeholder="Type something to analyze sentiment..."
          style={{ 
            width: '100%', 
            height: '100px',
            padding: '0.5rem',
            fontSize: '1rem',
            borderRadius: '4px',
            border: '1px solid #ccc'
          }}
        />
        <button 
          onClick={analyzeText}
          style={{
            marginTop: '0.5rem',
            padding: '0.5rem 1rem',
            background: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Analyze Text
        </button>
      </div>
      
      {result && (
        <div style={{ 
          marginBottom: '2rem',
          padding: '1rem',
          background: '#f8f9fa',
          borderRadius: '4px'
        }}>
          <h3>Analysis Result:</h3>
          <p>Sentiment: <strong>{result.sentiment}</strong></p>
          <p>Polarity Score: <strong>{result.polarity.toFixed(2)}</strong></p>
        </div>
      )}

      <div style={{ marginBottom: '2rem' }}>
        <h2>Sentiment Distribution</h2>
        <div style={{ height: '300px' }}>
          <Pie data={chartData} />
        </div>
      </div>
      
      <div>
        <h2>Recent Analyses</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {history.map((item, index) => (
            <li 
              key={index}
              style={{ 
                marginBottom: '0.5rem',
                padding: '0.5rem',
                background: index % 2 === 0 ? '#f8f9fa' : 'white',
                borderRadius: '4px'
              }}
            >
              "{item.text}" â†’ <strong style={{
                color: item.sentiment === 'positive' ? '#4CAF50' : 
                      item.sentiment === 'negative' ? '#F44336' : '#9E9E9E'
              }}>{item.sentiment}</strong>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
